template<oc_uint N>
[[nodiscard]] oc_bool oc_any(oc_array<oc_bool, N> arg) {
    oc_bool ret = arg[0];
    for(oc_uint i = 1u; i < N; ++i) {
        ret = ret || arg[i];
    }
    return ret;
}

template<oc_uint N>
[[nodiscard]] oc_bool oc_all(oc_array<oc_bool, N> arg) {
    oc_bool ret = arg[0];
    for(oc_uint i = 1u; i < N; ++i) {
        ret = ret && arg[i];
    }
    return ret;
}

template<oc_uint N>
[[nodiscard]] oc_bool oc_none(oc_array<oc_bool, N> arg) {
    return !oc_any(arg);
}


